css:
.sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.layout-mode-popout .sheet-toolbar {
  display: none;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #41403b, #34332f);
  box-shadow: none;
}
.sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #f6f6f6;
}
.sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
.container-signatures {
  max-width: 640px;
  margin: 0 auto;
}
.container-signatures .signature-wrap {
  position: relative;
  border: 1px solid rgba(230, 230, 230, 0.9);
  background-color: #ffffff;
  padding: 10px;
  margin-top: 20px;
  min-height: 200px;
  display: flex;
  flex-direction: column;
}
.container-signatures .signature-wrap textarea {
  border: 0;
  padding: 0;
  flex: 1;
  font-family: monospace;
  font-size: 0.9em;
}
.container-signatures .section-body {
  padding: 10px 0 0 0;
}
.container-signatures .section-body .menu {
  border: solid thin #CCC;
  margin-right: 5px;
  min-height: 200px;
}
.container-signatures .section-body .menu .menu-items {
  margin: 0;
  padding: 0;
  list-style: none;
}
.container-signatures .section-body .menu .menu-items li {
  padding: 6px;
}
.container-signatures .section-body .menu-horizontal {
  height: 100%;
}
.container-signatures .section-body .menu-horizontal .menu-items {
  height: 100%;
  margin: 0;
  padding: 0;
  list-style: none;
}
.container-signatures .section-body .menu-horizontal .menu-items li {
  text-align: center;
  width: 40px;
  display: inline-block;
  padding: 8px 16px 8px 16px;
  border-right: solid thin #CCC;
}
.container-signatures .section-body .signature-area {
  border: solid thin #CCC;
  min-height: 200px;
}
.container-signatures .section-body .menu-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.container-signatures .section-body .signature-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.container-signatures .section-body .signature-footer .edit-html-button {
  float: right;
  margin: 6px;
}
