css:
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(221, 221, 221, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(221, 221, 221, 0.8);
}
.keybase .no-keys-message {
  text-align: center;
}
.container-keybase {
  max-width: 640px;
  margin: 0 auto;
}
.keybase-profile {
  border: 1px solid #e6e6e6;
  border-top: 0;
  background: #ffffff;
  padding: 10px;
  overflow: auto;
  display: flex;
}
.keybase-profile .profile-photo-wrap {
  width: 50px;
  height: 50px;
  border-radius: 3px;
  padding: 3px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.5);
  background: #ffffff;
}
.keybase-profile .profile-photo-wrap .profile-photo {
  border-radius: 2px;
  overflow: hidden;
  text-align: center;
  width: 44px;
  height: 44px;
}
.keybase-profile .profile-photo-wrap .profile-photo img,
.keybase-profile .profile-photo-wrap .profile-photo .default-profile-image {
  width: 44px;
  height: 44px;
}
.keybase-profile .profile-photo-wrap .profile-photo .default-profile-image {
  line-height: 44px;
  font-size: 18px;
  font-weight: 500;
  color: white;
  box-shadow: inset 0 0 1px rgba(0, 0, 0, 0.18);
  background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0) 100%);
}
.keybase-profile .profile-photo-wrap .profile-photo .user-picture {
  background: #f6f6f6;
  width: 44px;
  height: 44px;
}
.keybase-profile .key-actions {
  display: flex;
  flex-direction: column;
}
.keybase-profile .key-actions button {
  margin: 2px 0 2px 10px;
  white-space: nowrap;
  display: inline-block;
  float: right;
}
.keybase-profile .details {
  margin-left: 10px;
  flex: 1;
}
.keybase-profile button {
  margin: 10px 0 10px 10px;
  white-space: nowrap;
  display: inline-block;
  float: right;
}
.keybase-profile keybase-participant-field {
  float: right;
}
.keybase-profile ul {
  list-style-type: none;
}
.keybase-profile .email-list {
  padding-left: 10px;
  word-break: break-all;
  flex-grow: 3;
  text-align: right;
}
.keybase-profile:first-child {
  border-top: 1px solid #e6e6e6;
}
.fixed-popover-container .keybase-participant-field,
.email-list .keybase-participant-field {
  margin-bottom: 10px;
}
.fixed-popover-container .keybase-participant-field .n1-keybase-recipient-key-chip,
.email-list .keybase-participant-field .n1-keybase-recipient-key-chip {
  display: none;
}
.fixed-popover-container .keybase-participant-field .tokenizing-field-label,
.email-list .keybase-participant-field .tokenizing-field-label {
  display: none;
  padding-top: 0;
}
.fixed-popover-container .keybase-participant-field .tokenizing-field-input,
.email-list .keybase-participant-field .tokenizing-field-input {
  padding-left: 0;
  padding-top: 0;
}
.fixed-popover-container .keybase-participant-field .tokenizing-field-input input,
.email-list .keybase-participant-field .tokenizing-field-input input {
  border: none;
}
.fixed-popover-container .keybase-participant-field {
  width: 300px;
  background: #ffffff;
  border: 1px solid rgba(230, 230, 230, 0.9);
}
.fixed-popover-container .keybase-participant-field .menu .content-container {
  background: #f6f6f6;
}
.fixed-popover-container .passphrase-popover {
  margin: 10px;
  display: flex;
}
.fixed-popover-container .passphrase-popover button {
  margin-left: 5px;
  flex: 0;
}
.fixed-popover-container .passphrase-popover input {
  min-width: 180px;
  flex: 1;
}
.fixed-popover-container .keybase-import-popover {
  margin: 10px;
}
.fixed-popover-container .keybase-import-popover button {
  width: 100%;
}
.fixed-popover-container .keybase-import-popover .title {
  margin: 0 auto;
  white-space: nowrap;
}
.email-list .keybase-participant-field {
  width: 200px;
  border-bottom: 1px solid #77828d;
}
.keybase-decrypt div.line-w-label {
  display: flex;
  align-items: center;
  color: rgba(128, 128, 128, 0.5);
}
.keybase-decrypt div.decrypt-bar {
  padding: 5px;
  border: 1.5px solid rgba(128, 128, 128, 0.5);
  border-radius: 5px;
  align-items: center;
  display: flex;
}
.keybase-decrypt div.decrypt-bar .title-text {
  flex: 1;
  margin: auto 0;
}
.keybase-decrypt div.decrypt-bar .decryption-interface button {
  margin-left: 5px;
}
.keybase-decrypt div.border {
  height: 1px;
  background: rgba(128, 128, 128, 0.5);
  flex: 1;
}
.key-manager div.line-w-label {
  display: flex;
  align-items: center;
  color: rgba(128, 128, 128, 0.5);
  margin: 10px 0;
}
.key-manager div.title-text {
  padding: 0 10px;
}
.key-manager div.border {
  height: 1px;
  background: rgba(128, 128, 128, 0.5);
  flex: 1;
}
.key-status-bar {
  background-color: #fcf4db;
  color: #695209;
  border: 1.5px solid #f8e6ac;
  border-radius: 2px;
  font-size: 13.02px;
  margin-bottom: 10px;
}
.key-add {
  padding-top: 10px;
}
.key-add .no-keys-message {
  text-align: center;
}
.key-add .key-adder {
  position: relative;
  border: 1px solid rgba(230, 230, 230, 0.9);
  padding: 10px;
  padding-top: 0;
  margin-bottom: 10px;
}
.key-add .key-adder .key-text {
  margin-top: 10px;
  min-height: 200px;
  display: flex;
}
.key-add .key-adder .key-text .loading {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, 50%);
}
.key-add .key-adder .key-text textarea {
  border: 0;
  padding: 0;
  font-size: 0.9em;
  flex: 1;
}
.key-add .credentials {
  display: flex;
  flex-direction: horizontal;
}
.key-add .credentials .key-add-btn {
  margin: 10px 5px 0 0;
  flex: 0;
}
.key-add .credentials .key-email-input {
  margin: 10px 5px 0 0;
  flex: 1;
}
.key-add .credentials .key-passphrase-input {
  margin: 10px 5px 0 0;
  flex: 1;
}
.key-add .credentials .invalid-msg {
  color: #AAA;
  white-space: nowrap;
  text-align: right;
  margin: 12px 5px 0 0;
  flex: 1;
}
.key-add .key-creation-button {
  display: inline-block;
  margin: 0 5px 10px 5px;
}
.key-add .editor-note {
  color: #AAA;
}
.key-instructions {
  color: #333;
  font-size: small;
  margin-top: 20px;
}
.keybase-search {
  margin-top: 15px;
  margin-bottom: 15px;
  overflow: scroll;
  position: relative;
}
.keybase-search input {
  padding: 10px;
  margin-bottom: 10px;
}
.keybase-search .empty {
  text-align: center;
}
.keybase-search .loading {
  position: absolute;
  right: 10px;
  top: 8px;
}
.keybase-search .bad-search-msg {
  display: inline-block;
  width: 100%;
  text-align: center;
  color: rgba(128, 128, 128, 0.5);
}
.keybase-search .bad-search-msg br {
  display: none;
}
.key-picker-modal {
  width: 400px;
  height: 400px;
  display: flex;
  flex-direction: column;
}
.key-picker-modal .keybase-search {
  display: flex;
  flex-direction: column;
  height: 100%;
  margin-bottom: 0;
  margin-top: 10px;
}
.key-picker-modal .keybase-search .searchbar {
  width: 380px;
  margin-left: auto;
  margin-right: auto;
}
.key-picker-modal .keybase-search .loading {
  right: 20px;
}
.key-picker-modal .keybase-search .results {
  overflow: scroll;
  height: 100%;
  width: 100%;
}
.key-picker-modal .keybase-search .bad-search-msg br {
  display: inline;
}
.key-picker-modal .picker-controls {
  width: 380px;
  margin: 5px auto 10px auto;
  display: flex;
  flex-shrink: 0;
  flex-direction: row;
}
.key-picker-modal .picker-controls .modal-back-button {
  float: left;
}
.key-picker-modal .picker-controls .modal-prefs-button {
  flex: 1;
  margin: 0 35px;
}
.key-picker-modal .picker-controls .modal-next-button {
  float: right;
}
.key-picker-modal .keybase-profile-solo {
  border: 1px solid #e6e6e6;
  margin-top: 10px;
}
.key-picker-modal .picker-title {
  margin-top: 10px;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
}
.decrypted {
  display: block;
  box-sizing: border-box;
  -webkit-print-color-adjust: exact;
  padding: 8px 12px;
  margin-bottom: 5px;
  border: 1px solid #ebccd1;
  border-radius: 4px;
  background-color: #79d45b;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
