css:
.sheet-toolbar {
  border-bottom: none;
  box-shadow: 0 0 0.5px #313236, 0 1px 1.5px #313236, 0 0 3px #313236;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.popover {
  border: 1px solid #313236;
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
.container-signatures {
  max-width: 640px;
  margin: 0 auto;
}
.container-signatures .signature-wrap {
  position: relative;
  border: 1px solid rgba(49, 50, 54, 0.9);
  background-color: #0a0b0c;
  padding: 10px;
  margin-top: 20px;
  min-height: 200px;
  display: flex;
  flex-direction: column;
}
.container-signatures .signature-wrap textarea {
  border: 0;
  padding: 0;
  flex: 1;
  font-family: monospace;
  font-size: 0.9em;
}
.container-signatures .section-body {
  padding: 10px 0 0 0;
}
.container-signatures .section-body .menu {
  border: solid thin #CCC;
  margin-right: 5px;
  min-height: 200px;
}
.container-signatures .section-body .menu .menu-items {
  margin: 0;
  padding: 0;
  list-style: none;
}
.container-signatures .section-body .menu .menu-items li {
  padding: 6px;
}
.container-signatures .section-body .menu-horizontal {
  height: 100%;
}
.container-signatures .section-body .menu-horizontal .menu-items {
  height: 100%;
  margin: 0;
  padding: 0;
  list-style: none;
}
.container-signatures .section-body .menu-horizontal .menu-items li {
  text-align: center;
  width: 40px;
  display: inline-block;
  padding: 8px 16px 8px 16px;
  border-right: solid thin #CCC;
}
.container-signatures .section-body .signature-area {
  border: solid thin #CCC;
  min-height: 200px;
}
.container-signatures .section-body .menu-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.container-signatures .section-body .signature-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.container-signatures .section-body .signature-footer .edit-html-button {
  float: right;
  margin: 6px;
}
